const sqlData = $node["SQL Query"].json || {};
const aiData = $node["OpenAI"].json.output || "Contact us for more details!";

// Add line breaks for readability
const formattedDescription = aiData
    .replace(/\n\n/g, '<br><br>')  // Converts double line breaks to HTML paragraph breaks
    .replace(/\n/g, ' ');           // Converts single line breaks to spaces

// Construct the structured JSON response
return {
    json: {
        marketing_description: sqlData.marketing_description || "Property Coming Soon!",
        advertised_rent: sqlData.advertised_rent || "N/A",
        bedrooms: sqlData.bedrooms || "N/A",
        bathrooms: sqlData.bathrooms || "N/A",
        sqft: sqlData.sqft || "Not specified",
        unit_address: sqlData.unit_address || "Address not available",
        aiEnhancedDescription: formattedDescription,
        appliances: sqlData.appliances || "Not specified",
        application_fee: sqlData.application_fee || "N/A",
        default_deposit: sqlData.default_deposit || "N/A",
        property_link: sqlData.property_link || "#"
    }
};
