const records = $input.all(); // Get all records from previous node

// Function to format phone numbers
function formatPhoneNumber(phone) {
    if (!phone) return "";
    let cleaned = phone.replace(/[^0-9]/g, ""); // Remove non-numeric characters
    if (cleaned.length === 10) return `(${cleaned.slice(0,3)}) ${cleaned.slice(3,6)}-${cleaned.slice(6)}`;
    if (cleaned.length === 11 && cleaned.startsWith("1")) return `+1 (${cleaned.slice(1,4)}) ${cleaned.slice(4,7)}-${cleaned.slice(7)}`;
    return cleaned; // Return cleaned if it's not a standard 10/11-digit US number
}

// Function to convert Excel date codes to MM/DD/YYYY format
function excelDateToMMDDYYYY(serial) {
    if (!serial || isNaN(serial)) return "";
    const excelEpoch = new Date(1899, 11, 30); // Excel date epoch
    const date = new Date(excelEpoch.getTime() + serial * 86400000); // Convert serial to date
    return `${String(date.getMonth() + 1).padStart(2, '0')}/${String(date.getDate()).padStart(2, '0')}/${date.getFullYear()}`;
}

// Function to keep only the first email in the "Emails" field
function getFirstEmail(emailField) {
    if (!emailField) return "";
    const emails = emailField.split(/[,;\s]+/); // Split by commas, semicolons, or spaces
    return emails.length > 0 ? emails[0].trim() : "";
}

// Process records
const formattedRecords = records
    .map(record => record.json) // Extract JSON from input
    .filter(record => record["Tenant Type"] === "Financially Responsible") // Filter Tenant Type
    .map(record => ({
        ...record,
        "Phone Numbers": formatPhoneNumber(record["Phone Numbers"]), // Format phone numbers
        "Lease To": excelDateToMMDDYYYY(record["Lease To"]), // Convert Lease To date
        "Move-in": excelDateToMMDDYYYY(record["Move-in"]), // Convert Move-in date
        "Move-out": excelDateToMMDDYYYY(record["Move-out"]), // Convert Move-in date
        "Emails": getFirstEmail(record["Emails"]) // Keep only the first email
    }));

return formattedRecords.map(json => ({ json }));

const records = $input.all(); // Get all records from previous node

// Function to format dates to YYYY-MM-DD
function formatMoveOutDate(date) {
    if (!date) return null;
    return date.includes("/") ? date : new Date(date).toISOString().split("T")[0]; 
}

// Process only records with a valid Move-out date
const formattedRecords = records
    .map(record => record.json)
    .filter(record => record["Move-out"]) // Keep only records with a Move-out date
    .map(record => {
        // Ensure First Name and Last Name are not undefined
        const firstName = record["First Name"] ? record["First Name"].trim() : "";
        const lastName = record["Last Name"] ? record["Last Name"].trim() : "";
        const residentName = `${firstName} ${lastName}`.trim();

        // Ensure Property Name is not undefined
        const propertyName = record["Property Name"] || "";

        // Format Move-out date correctly
        const moveOutFormatted = formatMoveOutDate(record["Move-out"]);

        // Create a valid JSON object (DO NOT STRINGIFY)
        const columnValuesObject = {
            "status": { "index": 0 }, // Default status
            "date4": { "date": moveOutFormatted }, // Move-out Date
            "text_mknfm8c8": propertyName, // Property Name
            "text_mknfdxg0": residentName, // Resident Name
            "boolean_mknfa9m0": { "checked": false }, // eReturn
            "text_mknf4476": record["Tenant Street Address 1"] ? 
                `${record["Tenant Street Address 1"]}, ${record["Tenant City"]}, ${record["Tenant State"]} ${record["Tenant Zip"]}`.trim() : null, // Full Address
            "text_mknfkj1x": record["Forwarding Address"] || null, // Forwarding Address
            "text_mknfsn9v": record["Move-out Reason"] || null // Move-out Reason
        };

        return {
            json: {
                itemName: `${propertyName} - ${residentName}`.trim(),
                columnValues: columnValuesObject // âœ… DO NOT STRINGIFY - Must remain an object!
            }
        };
    });

return formattedRecords;
