// Function to convert Excel date to JS Date
function excelDateToJSDate(excelDate) {
  if (isNaN(excelDate) || excelDate === '') {
    console.log("Invalid Excel date:", excelDate);
    return null; // Return null if the date is invalid or empty
  }

  const baseDate = new Date(1900, 0, 1); // Excel base date: January 1, 1900
  return new Date(baseDate.getTime() + (excelDate - 2) * 24 * 60 * 60 * 1000); // Subtract 2 days for Excel's leap year bug and convert days to ms
}

// Function to format JS Date to MySQL DATE format (YYYY-MM-DD)
function formatDateToMySQL(jsDate) {
  if (!jsDate || isNaN(jsDate.getTime())) {
    return null; // Return null for invalid dates
  }

  const day = String(jsDate.getDate()).padStart(2, '0');
  const month = String(jsDate.getMonth() + 1).padStart(2, '0'); // Months are 0-indexed
  const year = jsDate.getFullYear();
  return `${year}-${month}-${day}`; // Return in YYYY-MM-DD format
}

